<script>
    import { setCanvas, init, mousemove, startAnimation, stopAnimation } from '~/utils/neon-cover.mjs'

    /**
    * @see https://developer.mozilla.org/ru/docs/Web/API/Web_Animations_API/Using_the_Web_Animations_API
    */
    export default {
        computed: {
            copyright() {
                return ` ${new Date().getFullYear()} Netaggregator`
            },
            mobile() {
                return !!(this.$BROWSER || { IS_MOBILE: true }).IS_MOBILE
            },
            smallscreen() {
                // const { window: size } = this.$store.state.app
                return this.mobile

                /* return size
                    ? this.mobile && size.diagonal < 927
                    : this.mobile */
            },
            needInstall() {
                return !this.installed
            }
        },
        data: () => ({
            installed: false,
            animation: false,
            visible: false,
            neon: false,

            animations: [],

            links1:  [
                { path: '/chatbot'  },
                { path: '/stream'   },
                { path: '/widget'   },
                { path: '/cover'    },
                { path: '/designer' }
            ],
            links2: [
                'help',
                'referrer',
                'agreement',
                'privacy',
                'payment'
            ]
        }),
        watch: {
            needInstall: 'install',

            visible(v) {
                v ? startAnimation(this.neon) : stopAnimation(this.neon)
            },
            animation(v) {
                this.$bus.$emit('animation:pause', !v)
                this.$ls.set('zimed:animation', v)
                this[v ? 'play' : 'pause']()
            },
            neon(v) {
                this.$ls.set('zimed:neon', v)
                this.install()
            },

            '$store.state.app.window'() {
                if (this.smallscreen) return

                if (this.needInstall) {
                    this.install()
                } else {
                    init()

                    this.visible
                        ? startAnimation(this.neon)
                        : stopAnimation(this.neon)
                }
            }
        },
        methods: {
            setAnimations()
            {
                this.animations = document.getAnimations()
                    .filter(a => a.effect.getTiming().iterations === Infinity)
                    .filter(a => !a.animationName.startsWith('progress'))
            },
            onScroll(event = {})
            {
                if (this.smallscreen) return

                const offsetTop = event.target?.scrollingElement.scrollTop || 0,
                    html = document.documentElement,
                    body = document.body

                const height = Math.max(body.scrollHeight, body.offsetHeight, html.clientHeight, html.scrollHeight, html.offsetHeight),
                    footerHeight = this.$el?.clientHeight || 0

                const visible = height - footerHeight < offsetTop + window.innerHeight

                if (this.visible) {
                    init()
                }

                this.visible = visible
            },
            install()
            {
                if (!this.$refs.c1 || !this.$refs.c2) return

                document.removeEventListener('mousemove', this.mousemove)

                setCanvas([this.$refs.c1, this.$refs.c2])
                this.$bus.$emit('zimed:grid', init())

                this.installed = true

                if (this.neon) {
                    document.addEventListener('mousemove', this.mousemove)

                    this.visible
                        ? startAnimation(true)
                        : stopAnimation(true)

                } else {
                    stopAnimation(false)
                }
            },
            mousemove(e)
            {
                this.neon && this.visible && mousemove(e)
            },
            open(link)
            {
                switch (link) {
                    case 'referrer':
                        this.$bus.$emit('show:ref')
                        break
                    case 'agreement':
                        this.$bus.$emit('show:doc', { id: 0 })
                        break
                    case 'privacy':
                        this.$bus.$emit('show:doc', { id: 1 })
                        break
                    case 'payment':
                        this.$bus.$emit('show:doc', { id: 2 })
                        break
                    case 'help':
                        this.$bus.$emit('show:help')
                        break
                }
            },
            play()
            {
                this.animations.length || this.setAnimations()

                this.animations.forEach(animation => {
                    if (animation.playState === 'paused') {
                        animation.play()
                    }
                })
            },
            pause()
            {
                this.animations.length || this.setAnimations()

                this.animations.forEach(animation => {
                    if (animation.playState === 'running') {
                        animation.pause()
                    }
                })
            }
        },
        beforeDestroy()
        {
            document.removeEventListener('mousemove', this.mousemove)

            this.installed = false
            this.visible = false
        },
        async beforeMount()
        {
            try {

                const [animation, neon] = await this.$ls.getMany(
                    ['zimed:animation', 'zimed:neon'],
                    [true, true]
                )

                this.animation = animation
                this.neon = neon

            } catch (e) {
            }
        },
        async mounted()
        {
            await this.$nextTick()

            if (!this.smallscreen) {
                this.install()
            }
            if (this.mobile || !this.animation) {
                this.pause()
            }

            this.$bus.$emit('loading:finish')
        }
    }
</script>

