window.requestAnimationFrame = window.requestAnimationFrame || window.webkitRequestAnimationFrame || window.mozRequestAnimationFrame

let canvas = document.getElementById('canvas'),
    ctx = canvas.getContext('2d'),

    w = canvas.width = window.innerWidth,
    h = canvas.height = window.innerHeight,

    dataset = [],
    pIndex = 0,
    size = 1,
    hue = 0,

    logo = new Image()

function Particle(x, y)
{
    this.origX = this.x = x
    this.origY = this.y = y

    this.color = 'white'

    this.maxLife = this.random(15)
    this.life = 0

    this.vx = this.random(-1, 1)
    this.vy = this.random(-1, 1)

    this.grav = .07
    this.index = pIndex

    dataset[pIndex] = this
    pIndex++
}

Particle.prototype = {
    constructor: Particle,

    draw() {
        ctx.fillStyle = this.color
        ctx.fillRect(this.x, this.y, size, size)
        this.update()
    },

    update() {
        if (this.life >= this.maxLife) {
            dataset[this.index].reset()
        }

        this.x += this.vx
        this.y += this.vy
        this.vy += this.grav
        this.life++
    },

    reset() {
        this.x = this.origX
        this.y = this.origY

        this.life = 0
        this.color = `hsl(${hue}, 100%, 50%)`

        this.vx = this.random(-1, 1)
        this.vy = this.random(-1, 1)
    },

    random(...args) {
        const min = args.length === 1 ? 0 : args[0],
            max = args.length === 1 ? args[0] : args[1]

        return Math.random() * (max - min) + min
    }
}

function animate()
{
    window.requestAnimationFrame(animate)

    ctx.fillStyle = 'rgba(0,0,0,.1)'
    ctx.fillRect(0, 0, w, h)

    for (const i in dataset) {
        dataset[i].draw()
    }

    hue += 1
}

logo.src = 'data:image/png;base64,'

logo.onload = function() {
    const posX = (w - this.width) / 2,
        posY = (h - this.height) / 2

    ctx.drawImage(this, posX, posY)

    const imgData = ctx.getImageData(0, 0, w, h),
        pixels = imgData.data

    for (let y = 0; y < imgData.height; y += 3) {
        for (let x = 0; x < imgData.width; x += 3) {
            const alpha = pixels[((imgData.width * y) + x) * 4 + 3]

            if (alpha > 0) {
                dataset.push(new Particle(x, y))
            }
        }
    }

    animate()
}
